Rules.InvariantScript:
$"
    <div style='
            width: { Substitute(Self.Width - 11, ",", ".") }px;
            height: { Substitute(Self.Height - 9, ",", ".") }px;
            border-radius: 5px;
            box-shadow: rgba(0, 0, 0, 0.16) 0px 1px 4px;
            margin-top: 5px;
            margin-left: 5px;
            background-color: 
            { 
                Switch(
                    ThisItem.notificationtype,
                    "Success", 'Toast Notification 2'.Styles.Colors.Success,
                    "Warning", 'Toast Notification 2'.Styles.Colors.Warning,
                    "Error", 'Toast Notification 2'.Styles.Colors.Error,
                    "Info", 'Toast Notification 2'.Styles.Colors.Info
                )
            };
        '
    >
    </div>
"

Set(IsVisible, false); 
//Reset(timNotificationVisibility);
Set(remove,ThisItem);
Remove(Notifications,remove);
Reset('Toast Notification 2');
Navigate(App.ActiveScreen);
